def replaceWord(fileName,origWord,repWord):
	file = open(getMediaPath(fileName),"rt")
	outFile = open(getMediaPath("out-" + fileName),"wt")
	line = file.readline()
	while line <> '':
		newLine = line.replace(origWord,repWord);
		outFile.write(newLine)
		line = file.readline()
	file.close()
	outFile.close()

