def copy(source,target,start):
	 targetIndex = start
	 for sourceIndex in range(0,getLength(source)):
		 sourceValue = getSampleValueAt(source,sourceIndex)
		 setSampleValueAt(target,targetIndex,sourceValue)
		 targetIndex = targetIndex + 1
